import { IconButton } from "@chakra-ui/button";
import { Flex, VStack, Heading, Spacer } from "@chakra-ui/react";
import {
  FaSun,
  FaMoon,
  FaGithub,
  FaStackOverflow,
  FaDev,
} from "react-icons/fa";
import { useColorMode } from "@chakra-ui/color-mode";
import Header from "./components/Header";
import Profile from "./components/Profile";
import Social from "./components/Social";

function App() {
  const { colorMode, toggleColorMode } = useColorMode();
  const isDark = colorMode === "dark";

  return (
    <VStack p={5}>
      <Flex w="100%">
        <Heading ml="8" size="md" fontWeight="semibold" color="cyan.400">
          Lakshya's Portfolio
        </Heading>
        <Spacer></Spacer>

        <IconButton
          ml={2}
          icon={<FaGithub />}
          isRound="true"
          onClick={() => window.open("https://github.com/kenkirito")}
        ></IconButton>

        <IconButton
          ml={2}
          icon={<FaStackOverflow />}
          isRound="true"
          onClick={() =>
            window.open("https://stackoverflow.com/users/16427979/lakshya")
          }
        ></IconButton>

        <IconButton
          ml={2}
          icon={<FaDev />}
          isRound="true"
          onClick={() => window.open("https://dev.to/kenkirito")}
        ></IconButton>

        <IconButton
          ml={8}
          icon={isDark ? <FaSun /> : <FaMoon />}
          isRound="true"
          onClick={toggleColorMode}
        ></IconButton>
      </Flex>
      <Header />
      <Social />
      <Profile />
    </VStack>
  );
}

export default App;
